mapper.select:
SELECT wid, a. NAME AS `name`, type, b.`NAME` AS typeName, cost, DATE_FORMAT(START_TIME,'%Y-%m-%d') as startTime, DATE_FORMAT(END_TIME,'%Y-%m-%d') as endTime, CONCAT(DATE_FORMAT(START_TIME,'%Y-%m-%d'), '~', DATE_FORMAT(END_TIME,'%Y-%m-%d')) AS activityTime, date_format(UPDATE_TIME,'%Y-%m-%d %H:%i') AS updateTime, a.region, c.full_name as regionName FROM tb_activity_info a LEFT JOIN tb_dic_activity_type b ON a.TYPE = b.id left join tb_dic_xj_xzqh c on IF ( LOCATE(',', a.REGION) != 0, RIGHT ( a.REGION, LENGTH(REGION) - LOCATE(',', a.REGION) ), a.REGION )=c.id where 1=1
and a.type=#{type}
and a.name=#{name}

SELECT wid, a. NAME AS `name`, cost, DATE_FORMAT(START_TIME,'%Y-%m-%d') as startTime, DATE_FORMAT(END_TIME,'%Y-%m-%d') as endTime, CONCAT(DATE_FORMAT(START_TIME,'%Y-%m-%d'), '~', DATE_FORMAT(END_TIME,'%Y-%m-%d')) AS activityTime, date_format(UPDATE_TIME,'%Y-%m-%d %H:%i') AS updateTime, a.region,b.full_name as regionName FROM tb_activity_info a LEFT JOIN tb_dic_xj_xzqh b ON IF ( LOCATE(',', a.REGION) != 0, RIGHT ( a.REGION, LENGTH(REGION) - LOCATE(',', a.REGION) ), a.REGION )=b.id where 1=1
and a.type=#{type}
order by CREATE_TIME desc, CONVERT( a.name USING gbk )
