mapper.select:
SELECT wid, a. NAME AS `name`, type, b.`NAME` AS typeName, REGION AS region, area, COST AS cost, date_format(UPDATE_TIME,'%Y-%m-%d %H:%i') AS updateTime, date_format(a.start_time,'%Y-%m-%d') as startTime, date_format(a.end_time,'%Y-%m-%d') as endTime, concat(date_format(a.start_time,'%Y-%m-%d'),'~',date_format(end_time,'%Y-%m-%d')) as businessTime, c.full_name as regionName FROM tb_activity_room_info a LEFT JOIN tb_dic_activity_room_type b ON a.type = b.ID left join tb_dic_xj_xzqh c on IF ( INSTR(REVERSE(region), ',') != 0, REVERSE( LEFT ( REVERSE(region), INSTR(REVERSE(region), ',') - 1 ) ), a.REGION )=c.id where 1=1
and type=#{type}
and INSTR(name,#{name})
