mapper.select:
SELECT a.wid, e. NAME AS `name`, TIMESTAMPDIFF( YEAR, CONCAT(e.BIRTH_DATE), NOW() ) AS age, a. STATUS AS `status`, c. NAME AS statusName, date_format(START_TIME,'%Y-%m-%d %H:%i') AS startTime, date_format(END_TIME,'%Y-%m-%d %H:%i') AS endTime, CONCAT( DATE_FORMAT( START_TIME, '%Y-%m-%d %H:%i' ), '至', DATE_FORMAT(END_TIME, '%Y-%m-%d %H:%i') ) AS serviceTime, date_format(a.CREATE_TIME,'%Y-%m-%d %H:%i') AS createTime, f.full_name AS regionName, a.DESCRIBE as `describe` FROM tb_Order_info a LEFT JOIN tb_user_info b ON a.MAIN_ID = b.wid LEFT JOIN tb_dic_order_status c ON a.`STATUS` = c.ID LEFT JOIN tb_dic_score d ON a.SCORE >= d.`START` and a.SCORE <= d.`END` LEFT JOIN tb_elderly_info e ON a.ELDERLY_ID = e.WID LEFT JOIN tb_dic_xj_xzqh f ON IF ( INSTR(REVERSE(e.region), ',') != 0, REVERSE( LEFT ( REVERSE(e.region), INSTR(REVERSE(e.region), ',') - 1 ) ), e.REGION ) = f.id WHERE 1 = 1 and b.wid is not null
and case when #{status}='5' or #{status}='2,4' then a.status !='1' else a.status=#{status} end
and d.id=#{score}
and a.user_wid=#{userWid}
and a.type=#{type}

SELECT a.wid, e. NAME AS `name`, TIMESTAMPDIFF( YEAR, CONCAT(e.BIRTH_DATE), NOW() ) AS age, a. STATUS AS `status`, case when a.status ='2' then '预约成功' else c. NAME end AS statusName, a.START_TIME AS startTime, a.END_TIME AS endTime, CONCAT( a.START_TIME ,'至',a.END_TIME )AS serviceTime, date_format(a.CREATE_TIME,'%Y-%m-%d %H:%i') AS createTime, g.name AS typeName, b.type, b.name as activityRoomName FROM tb_Order_info a LEFT JOIN tb_activity_room_info b ON a.MAIN_ID = b.wid left join tb_dic_activity_room_type g on b.type=g.id LEFT JOIN tb_dic_order_status c ON a.`STATUS` = c.ID LEFT JOIN tb_dic_score d ON a.SCORE >= d.`START` and a.SCORE <= d.`END` LEFT JOIN tb_elderly_info e ON a.ELDERLY_ID = e.WID LEFT JOIN tb_dic_xj_xzqh f ON IF ( INSTR(REVERSE(e.region), ',') != 0, REVERSE( LEFT ( REVERSE(e.region), INSTR(REVERSE(e.region), ',') - 1 ) ), e.REGION ) = f.id WHERE 1 = 1 and b.wid is not null
and case when #{status}='5' or #{status}='2,4' then a.status !='1' else a.status=#{status} end
and d.id=#{score}
and a.user_wid=#{userWid}
and a.type=#{type}

SELECT a.wid, e. NAME AS `name`, TIMESTAMPDIFF( YEAR, CONCAT(e.BIRTH_DATE), NOW() ) AS age, a. STATUS AS `status`, case when a.status ='2' then '预约成功' else c. NAME end AS statusName, date_format(a.START_TIME,'%Y-%m-%d') AS startTime, date_format(a.END_TIME,'%Y-%m-%d') AS endTime, CONCAT( DATE_FORMAT( a.START_TIME, '%Y-%m-%d %H:%i' ), '至', DATE_FORMAT(a.END_TIME, '%Y-%m-%d') ) AS serviceTime, date_format(a.CREATE_TIME,'%Y-%m-%d') AS createTime, g.name as typeName, b.type, b.cost, b.name as activityName
FROM tb_Order_info a LEFT JOIN tb_activity_info b ON a.MAIN_ID = b.wid left join tb_dic_activity_type g on b.type=g.id LEFT JOIN tb_dic_order_status c ON a.`STATUS` = c.ID LEFT JOIN tb_dic_score d ON a.SCORE >= d.`START` and a.SCORE <= d.`END` LEFT JOIN tb_elderly_info e ON a.ELDERLY_ID = e.WID LEFT JOIN tb_dic_xj_xzqh f ON IF ( INSTR(REVERSE(e.region), ',') != 0, REVERSE( LEFT ( REVERSE(e.region), INSTR(REVERSE(e.region), ',') - 1 ) ), e.REGION ) = f.id WHERE 1 = 1 and b.wid is not null
and case when #{status}='5' or #{status}='2,4' then a.status !='1' else a.status=#{status} end
and d.id=#{score}
and a.user_wid=#{userWid}
and a.type=#{type}
