mapper.select:
SELECT wid, a. NAME AS `name`, sex, c. NAME AS sexName, TIMESTAMPDIFF( YEAR, CONCAT(BIRTH_DATE), NOW() ) AS age, phone, type, b. NAME AS typeName, create_time AS createTime FROM tb_user_info a LEFT JOIN tb_dic_user_type b ON a.type = b.ID LEFT JOIN tb_dic_user_sex c ON a.sex = c.id where 1=1
and a.type=#{type}
and a.name=#{name}

SELECT b.wid, IFNULL(avg(SCORE),'暂无') AS score, b.`NAME` AS `name`, GROUP_CONCAT(d.`NAME`) AS businessName, c. NAME AS typeName, COST AS cost FROM tb_order_info a right JOIN tb_user_info b ON a.MAIN_ID = b.WID LEFT JOIN tb_dic_user_type c ON b.type = c.id LEFT JOIN tb_dic_business d ON FIND_IN_SET(d.id, b.BUSINESS) > 0 where 1=1 and b.type!='1'
and b.type=#{type}
GROUP BY a.MAIN_ID ORDER BY score,CONVERT( b.name USING gbk ) LIMIT 0,5

SELECT a.wid, a. NAME AS `name`, IFNULL(AVG(b.SCORE), '暂无') AS score, GROUP_CONCAT(d.`NAME`) AS businessName, c. NAME AS typeName, COST AS cost FROM tb_user_info a left JOIN tb_order_info b ON b.MAIN_ID = a.WID LEFT JOIN tb_dic_user_type c ON b.type = c.id LEFT JOIN tb_dic_business d ON FIND_IN_SET(d.id, a.BUSINESS) > 0 LEFT JOIN tb_dic_age e ON TIMESTAMPDIFF( YEAR, CONCAT(a.BIRTH_DATE), now() ) BETWEEN e.`START` AND e.`end` WHERE 1 = 1
and a.type=#{type}
and FIND_IN_SET(#{business}, a.BUSINESS) > 0
and a.sex=#{sex}
and e.id=#{age}
GROUP BY a.wid
